const init_state = {
    id_user: 0,
    username: "",
    email: "",
    address: "",
    phoneNumber: "",
    fullName: "",
    gender: "",
    age: 0,
    profilePicture : "",
    role : "",
    status : ""
  };
  
  const reducer = (state = init_state, action) => {
    switch (action.type) {
      case "USER_LOGIN":
        return { ...state, ...action.payload };
      case "USER_ERROR":
          return { ...state, errMsg: action.payload };
      default:
        return state;
    }
  };
  
  export default reducer;
