import React from "react";
import Axios from "axios";
import {
  Col,
  Button,
  Form,
  FormGroup,
  Label,
  Input,
  FormText,
  Table,
} from "reactstrap";
import { connect } from "react-redux";
import { URL_API } from "../helper";
import image_preview from "../../src/img/image_preview.png";

class Profile extends React.Component {
  state = {
    address: "",
    phone_number: 0,
    full_name: "",
    gender: "",
    age: 0,
    profile_picture: "",
    role: "",
    status: "",
    pharma2: [],
    selectedID: null,
  };
  inputHandler = (event) => {
    const value = event.target.value;
    const name = event.target.name;
    this.setState({ [name]: value });
  };

  //   inputHandlerImg = (event) => {

  //     this.setState({ profile_picture: URL.createObjectURL(upload) });
  //   };

  componentDidMount() {
    this.getData();
  }

  getData = () => {
    // Axios.get(`http://localhost:3300/user/getProfile/$this.props.globalState.id_user`)
    Axios.get(`http://localhost:3300/user/getProfile/1`)
      .then((res) => {
        this.setState({ pharma2: res.data });

        this.setState({ address: res.data.address });
        this.setState({ phone_number: res.data.phone_number });
        this.setState({ full_name: res.data.full_name });
        this.setState({ gender: res.data.gender });
        this.setState({ age: res.data.age });
      })
      .catch((err) => {
        console.log(err);
      });
  };

  onImgPreview = () => {
    if (this.state.addFile) {
      let formData = new FormData();

      formData.append("file", this.state.addFile);
      Axios.patch(`${URL_API}/upload/uploadimg/1`, formData)
        .then((res) => {
          alert(res.data.message);
          this.getData();
        })
        .catch((err) => {
          console.log(err);
        });
    }
  };

  getProfileImage = () => {
    Axios.get(`${URL_API}/upload/get`)
      .then((res) => {
        this.setState({ profile_picture: res.data.profile_picture });
      })
      .catch((err) => {
        console.log(err);
      });
  };

  onBtnAddImg = () => {
    this.onBtnSave();
    this.onImgPreview();
  };

  onBtnAddfile = (e) => {
    if (e.target.files[0]) {
      this.setState({
        profile_picture: e.target.files[0].name,
        addFile: e.target.files[0],
      });
      let preview = document.getElementById("imgpreview");
      preview.src = URL.createObjectURL(e.target.files[0]);
    }
  };

  onBtnSave = () => {
    const { address, phone_number, full_name, gender, age, profile_picture } =
      this.state;
    console.log(address, phone_number, full_name, gender, age, profile_picture);
    Axios.patch(`${URL_API}/user/edit/1`, {
      address,
      phone_number,
      full_name,
      gender,
      age,
      profile_picture,
    })
      .then(() => {
        alert("Profile Change Successfully");
        this.getData();
        this.setState({ selectedID: null });
      })
      .catch((err) => {
        console.log(err);
      });
  };

  printData = () => {
    return this.state.pharma2.map((item, index) => {
      if (this.state.selectedID !== index) {
        return (
          <div class="container rounded bg-white mt-5 mb-5">
            <div class="row">
              <div class="col-md-3 border-right">
                <div class="d-flex flex-column align-items-center text-center p-3 py-5">
                  <img
                    class="rounded-circle mt-5"
                    width="150px"
                    src="https://st3.depositphotos.com/15648834/17930/v/600/depositphotos_179308454-stock-illustration-unknown-person-silhouette-glasses-profile.jpg"
                  />
                  <span class="font-weight-bold">Edogaru</span>
                  <span class="text-black-50">edogaru@mail.com.my</span>
                  <span> </span>
                </div>
              </div>
              <div class="col-md-5 border-right">
                <div class="p-3 py-5">
                  <div class="d-flex justify-content-between align-items-center mb-3">
                    <h4 class="text-right">Profile</h4>
                  </div>
                  <div class="row mt-2">
                    <div class="col-md-6">
                      <label class="labels">Username</label>
                      <input
                        type="text"
                        disabled="true"
                        defaultValue={item.username}
                        class="form-control"
                        placeholder="username"
                      />
                    </div>
                    <div class="col-md-6">
                      <label class="labels">Email</label>
                      <input
                        type="text"
                        disabled="true"
                        defaultValue={item.email}
                        class="form-control"
                        placeholder="surname"
                      />
                    </div>
                  </div>
                  <div class="row mt-3">
                    <div class="col-md-12">
                      <label class="labels">Addrees</label>
                      <input
                        type="text"
                        class="form-control"
                        placeholder="enter phone number"
                        value=""
                      />
                    </div>
                    <div class="col-md-12">
                      <label class="labels">Address Line 1</label>
                      <input
                        type="text"
                        class="form-control"
                        placeholder="enter address line 1"
                        value=""
                      />
                    </div>
                    <div class="col-md-12">
                      <label class="labels">Address Line 2</label>
                      <input
                        type="text"
                        class="form-control"
                        placeholder="enter address line 2"
                        value=""
                      />
                    </div>
                    <div class="col-md-12">
                      <label class="labels">Postcode</label>
                      <input
                        type="text"
                        class="form-control"
                        placeholder="enter address line 2"
                        value=""
                      />
                    </div>
                    <div class="col-md-12">
                      <label class="labels">State</label>
                      <input
                        type="text"
                        class="form-control"
                        placeholder="enter address line 2"
                        value=""
                      />
                    </div>
                    <div class="col-md-12">
                      <label class="labels">Area</label>
                      <input
                        type="text"
                        class="form-control"
                        placeholder="enter address line 2"
                        value=""
                      />
                    </div>
                    <div class="col-md-12">
                      <label class="labels">Email ID</label>
                      <input
                        type="text"
                        class="form-control"
                        placeholder="enter email id"
                        value=""
                      />
                    </div>
                    <div class="col-md-12">
                      <label class="labels">Education</label>
                      <input
                        type="text"
                        class="form-control"
                        placeholder="education"
                        value=""
                      />
                    </div>
                  </div>
                  <div class="row mt-3">
                    <div class="col-md-6">
                      <label class="labels">Country</label>
                      <input
                        type="text"
                        class="form-control"
                        placeholder="country"
                        value=""
                      />
                    </div>
                    <div class="col-md-6">
                      <label class="labels">State/Region</label>
                      <input
                        type="text"
                        class="form-control"
                        value=""
                        placeholder="state"
                      />
                    </div>
                  </div>
                  <div class="mt-5 text-center">
                    <button
                      class="btn btn-primary profile-button"
                      type="button"
                    >
                      Save Profile
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        );
      } else {
        return (
          <div className="container">
            <div className="col-12 text-center my-5">PROFILE PAGE</div>
            <div className="row mt-5">
              <div className="col-4 offset-4">
                <div
                  className="card"
                  style={{ backgroundColor: "#6495ED", width: "500px" }}
                >
                  <div className="card-body">
                    <div className="font-weight-bold mb-3">
                      <div
                        className="dflex justify-content-center align-items-center"
                        style={{ height: "40vh" }}
                      >
                        <div>
                          <img
                            name="img"
                            id="imgpreview"
                            src={URL_API + item.profile_picture}
                            className="justify-content-center"
                            alt=""
                            style={{
                              borderRadius: "50%",
                              width: "300px",
                              height: "300px",
                            }}
                          />
                        </div>
                        <div className="my-3">
                          <label htmlFor="formFile" className="form-label">
                            Upload Image Here
                          </label>
                          <input
                            onChange={this.onBtnAddfile}
                            type="file"
                            className="form-control"
                            id="formFile"
                            accept="image/*"
                          />
                        </div>
                      </div>

                      <Form>
                        <label className={"d-grid gap-2 my-1"}>Username</label>

                        <FormGroup>
                          <Input
                            className={
                              "justify-content-center align-items-center"
                            }
                            disabled="true"
                            type="text"
                            name="username"
                            innerRef={(newUsername) =>
                              (this.newUsername = newUsername)
                            }
                            defaultValue={item.username}
                            style={{ width: "400px" }}
                          />
                        </FormGroup>

                        <label className={"d-grid gap-2 my-1"}>Email</label>

                        <FormGroup>
                          <Input
                            className={
                              "justify-content-center align-items-center"
                            }
                            disabled="true"
                            type="text"
                            name="email"
                            innerRef={(newEmail) => (this.newEmail = newEmail)}
                            defaultValue={item.email}
                            style={{ width: "400px" }}
                          />
                        </FormGroup>

                        <label className={"d-grid gap-2 my-1"}>Address</label>

                        <FormGroup>
                          <Input
                            className={
                              "justify-content-center align-items-center"
                            }
                            onChange={this.inputHandler}
                            type="textarea"
                            name="address"
                            value={this.state.address}
                            defaultValue={item.address}
                            style={{ width: "400px" }}
                          />
                        </FormGroup>

                        <label className={"d-grid gap-2 my-1"}>
                          Phone Number
                        </label>

                        <FormGroup>
                          <Input
                            className={
                              "justify-content-center align-items-center"
                            }
                            onChange={this.inputHandler}
                            type="text"
                            name="phone_number"
                            value={this.state.phone_number}
                            defaultValue={item.phone_number}
                            style={{ width: "400px" }}
                          />
                        </FormGroup>

                        <label className={"d-grid gap-2 my-1"}>Full Name</label>

                        <FormGroup>
                          <Input
                            className={
                              "justify-content-center align-items-center"
                            }
                            onChange={this.inputHandler}
                            type="text"
                            name="full_name"
                            value={this.state.full_name}
                            defaultValue={item.full_name}
                            style={{ width: "400px" }}
                          />
                        </FormGroup>

                        <label className={"d-grid gap-2 my-1"}>Gender</label>

                        <FormGroup>
                          <Input
                            className={"d-grid mx-4"}
                            style={{ width: "100px" }}
                            onChange={this.inputHandler}
                            value={this.state.gender}
                            type="select"
                            name="gender"
                            id="exampleSelect"
                          >
                            <option value={"-"}>-</option>
                            <option value={"Pria"}>Pria</option>
                            <option value={"Wanita"}>Wanita</option>
                          </Input>
                        </FormGroup>

                        <label className={"d-grid gap-2 my-1"}>Age</label>

                        <FormGroup>
                          <Input
                            className={
                              "justify-content-center align-items-center"
                            }
                            onChange={this.inputHandler}
                            type="text"
                            name="age"
                            defaultValue={item.age}
                            value={this.state.age}
                            style={{ width: "400px" }}
                          />
                        </FormGroup>

                        <FormGroup>
                          {this.state.selectedID == null ? (
                            <>
                              <Button
                                onClick={() =>
                                  this.setState({ selectedID: index })
                                }
                              >
                                Edit
                              </Button>
                              <Button>Delete</Button>
                            </>
                          ) : (
                            <>
                              <Button
                                className={" gap-2 col-6 my-2 btn warning "}
                                onClick={() =>
                                  this.setState({ selectedID: null })
                                }
                              >
                                Back
                              </Button>
                              <Button
                                className={
                                  "d-grid gap-2 col-6 my-2 btn btn btn-success"
                                }
                                onClick={() => this.onBtnAddImg(this.state)}
                              >
                                Save Change
                              </Button>
                            </>
                          )}
                        </FormGroup>
                      </Form>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>
        );
      }
    });
  };

  render() {
    //    console.log( this.posisi)
    return (
      <div className="row m-auto">
        <div className="col-md-10">
          <Table>
            <tbody>{this.printData()}</tbody>
          </Table>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    userGlobal: state.user,
  };
};

export default connect(mapStateToProps)(Profile);
