import Axios from "axios";
import { URL_API } from "../../helper";


export const GET_USERs_DETAIL = "GET_USERS_DETAIL"

export const getUserDetail = () =>{
  return (dispatch) =>{
    Axios.get(`${URL_API}/user/login`)
    .then((res)=>{
      dispatch({
        type : GET_USERs_DETAIL,
        payload : {
          data : res.data,
          errorMessage : false
        }
      })
    })
    .catch((er))
  }

}
