import React from 'react';
import Axios from 'axios';
import { Col, Button, Form, FormGroup, Label, Input, FormText, Table } from 'reactstrap';
import {useParams} from 'react-router-dom'

class Profile extends React.Component{
       

constructor(props) {
    super(props);
    this.state = {
        pharma2: [],
        selectedID: null
    }
}

componentDidMount() {
    this.getData()
}

getData = () => {
    Axios.get('http://localhost:3300/user/getProfile')
        .then(res => {
            console.log(res.data)
            this.setState({ pharma2 : res.data })
        })
        .catch(err => {
            console.log(err)
        })
}


printData = () => {
    return this.state.pharma2.map((item, index) => {
        if (this.state.selectedID !== index) {
            return (
                <tr>
                    <td>{index + 1}</td>
                    <td>{item.username}</td>
                    <td>{item.email}</td>
                    <td>{item.address}</td>
                    <td>{item.phone_number}</td>
                    <td>{item.full_name}</td>
                    <td>{item.gender}</td>
                    <td>{item.age}</td>
                    <td><Button onClick={() => this.setState({ selectedID: index })}>Edit </Button><Button >Delete</Button></td>
                </tr>
            )
        } else {
            return (
                <tr>
                    <td>{index + 1}</td>
                    <td><Input type="text" name="text" innerRef={(newUsername) => this.newUsername = newUsername} defaultValue={item.username} /></td>
                    <td><Input type="text" name="text" innerRef={(newEmail) => this.newEmail = newEmail} defaultValue={item.email} /></td>
                    <td><Input type="text" name="text" innerRef={(newAddress) => this.newAddress = newAddress} defaultValue={item.address} /></td>
                    <td><Input type="text" name="text" innerRef={(newPhoneN) => this.newPhoneN = newPhoneN} defaultValue={item.phone_number} /></td>
                    <td><Input type="text" name="text" innerRef={(newFullN) => this.newFullN = newFullN} defaultValue={item.full_name} /></td>
                    <td> <Input type="select" name="select" id="exampleSelect" innerRef={(newGender) => this.newGender = newGender}>
                        <option value={1}>Pria</option>
                        <option value={2}>Wanita</option>
                    </Input> </td>
                    <td>{this.state.selectedID == null ? <><Button onClick={() => this.setState({ selectedID: index })}>Edit</Button><Button>Delete</Button></> :
                        <><Button onClick={() => this.setState({ selectedID: null })}>No</Button><Button >Yes</Button></>}</td>
                </tr>
            )
        }
    })
}

render() {
    //    console.log( this.posisi)
    return (
        <div className="row m-auto">
            <Form width='90vw' className="col-md-2">
                <FormGroup>
                    <Label for="nama">Username</Label>
                    <Input type="text" name="text" id="username" innerRef={(username) => this.username = username} />
                </FormGroup>
                <FormGroup>
                    <Label for="usia">Email</Label>
                    <Input type="number" id="email" innerRef={(email) => this.email = email} />
                </FormGroup>
                <FormGroup>
                    <Label for="email">Address</Label>
                    <Input type="text" id="address" innerRef={(address) => this.address = address} />
                </FormGroup>
                <FormGroup>
                    <Label for="berat">Phone Number</Label>
                    <Input type="number" name="text" id="berat" innerRef={(phone_number) => this.phone_number = phone_number} />
                </FormGroup>
                <FormGroup>
                    <Label for="kota">full name</Label>
                    <Input type="text" name="text" id="full_name" innerRef={(full_name) => this.full_name = full_name} />
                </FormGroup>
                <FormGroup>
                    <Label for="posisi">gender</Label>
                    <Input type="select" name="text" id="posisi" innerRef={(posisi) => this.posisi = posisi}>
                        <option value={1}>Pria</option>
                        <option value={2}>Wanita</option>
                    </Input>
                </FormGroup>
                <Button type='button' onClick={this.onBtAdd}>Submit</Button>
            </Form>
            <div className="col-md-10">
                <Table >
                    <thead>
                        <tr>
                            <th>#</th>
                            <th>username</th>
                            <th>email</th>
                            <th>address</th>
                            <th>phone number</th>
                            <th>fullname</th>
                            <th>gender</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.printData()}
                    </tbody>
                </Table>
            </div>
        </div>
    );
}
}



export default Profile;
