import React from "react";
import { Link, Redirect } from "react-router-dom";
import { connect } from 'react-redux';
import { URL_API } from "../../helper";
import Axios from 'axios'
import { NavItem, Button, Input } from "reactstrap";



class CustomTransaction extends React.Component{

    state = {
        dbCustomTransaction : [],
        
    }

    inputHandler = (e) =>{
        const value = e.target.value 
        const name = e.target.name 
        this.setState({[name] : value})

    }

    getData = () => {
        Axios.get(`${URL_API}/admin/custom-order`)
          .then((res) => {
            this.setState({
              dbCustomTransaction: res.data.results,
            });
          })
          .catch((err) => {
            alert("Cannot Get Data");
            console.log(err);
          });
      };

      componentDidMount (){
          this.getData()
      }

  



    printData = () =>{
        return this.state.dbCustomTransaction.map((item, index) =>{
          return(
            <tr>
              <td className="align-middle">
                  {index+1}
                </td>
                <td className="align-middle">
                    {item.username}
                </td>
                <td className="align-middle">
                {item.commentar}
                </td>
                <td className="align-middle">
                    <img
                     src={URL_API + item.prescription_img} alt="" style={{ height: "125px" }}  />
                
                </td>
              </tr>
          )
        })
      
      }
    render(){
        
        return(
            
            <div className="p-5 text-center" style={{scr}}>
                <div class="main" id="section1" style={{height : "800px", backgroundColor : "pink"}}>
                    <h2>Section 1</h2>
                    <p>Click on the link to see the "smooth" scrolling effect.</p>
                    <a href="#section2">Click Me to Smooth Scroll to Section 2 Below</a>
                    <h1> custom Transaction</h1>
        <div className="row mt-5">
          <div className="col-9 text-center">
         
            <table className="table">
              <thead className="thead-dark">
                <tr>
                  <th>No</th>
                  <th>Username</th>
                  <th>Commentar</th>
                  <th>Image</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                {this.printData()}
              </tbody>
              <tfoot className="bg-light">
                <tr>
                  <td colSpan="10">
                    
              <div className="d-flex flex-row justify-content-center align-items-center">
                <button
                  disabled={this.state.page === 1}
                  className="btn btn-dark"
                  onClick={this.prevPageHandler}
                >
                  {"<"}
                </button>
                <div className="text-center">
                 page
                </div>
                <button
                  disabled={this.state.dbCustomTransaction.length < 5}
                  className="btn btn-dark"
                  onClick={this.nextPageHandler}
                >
                  {">"}
                </button>
              </div>
                  </td>
                </tr>
              </tfoot>
            </table>
          </div>
        
        </div>
            </div>

            <div class="main" id="section2" style={{height : "800px", backgroundColor : "yellow"}}>
            <h2>Section 2</h2>
            <a href="#section1">Click Me to Smooth Scroll to Section 1 Above</a>
        </div>
        
      </div>
      
        )
    }
    
}
export default CustomTransaction
