import React from 'react';
import Axios from 'axios';
import { Col, Button, Form, FormGroup, Label, Input, FormText, Table } from 'reactstrap';


class Profile extends React.Component{
       

constructor(props) {
    super(props);
    this.state = {
        pharma2: [],
        selectedID: null
    }
}

componentDidMount() {
    this.getData()
}

getData = () => {
    Axios.get(`http://localhost:3300/user/getProfile/`)
        .then(res => {
            console.log(res.data)
            this.setState({ pharma2 : res.data })
        })
        .catch(err => {
            console.log(err)
        })
}

render(){
    
        return this.state.pharma2.map((item, index) => {
            if (this.state.selectedID !== index) {
                return (
                    <tr>
                        <td>{index + 1}</td>
                        <td>{item.username}</td>
                        <td>{item.email}</td>
                        <td>{item.address}</td>
                        <td>{item.phone_number}</td>
                        <td>{item.full_name}</td>
                        <td>{item.gender}</td>
                        <td>{item.age}</td>
                        <td><Button onClick={() => this.setState({ selectedID: index })}>Edit </Button><Button >Delete</Button></td>
                    </tr>
                )
            } else {
                return (
                    <tr>
                        <td>{index + 1}</td>
                        <td><Input type="text" name="text" innerRef={(newUsername) => this.newUsername = newUsername} defaultValue={item.username} /></td>
                        <td><Input type="text" name="text" innerRef={(newEmail) => this.newEmail = newEmail} defaultValue={item.email} /></td>
                        <td><Input type="text" name="text" innerRef={(newAddress) => this.newAddress = newAddress} defaultValue={item.address} /></td>
                        <td><Input type="text" name="text" innerRef={(newPhoneN) => this.newPhoneN = newPhoneN} defaultValue={item.phone_number} /></td>
                        <td><Input type="text" name="text" innerRef={(newFullN) => this.newFullN = newFullN} defaultValue={item.full_name} /></td>
                        <td> <Input type="select" name="select" id="exampleSelect" innerRef={(newGender) => this.newGender = newGender}>
                            <option value={1}>Pria</option>
                            <option value={2}>Wanita</option>
                        </Input> </td>
                        <td>{this.state.selectedID == null ? <><Button onClick={() => this.setState({ selectedID: index })}>Edit</Button><Button>Delete</Button></> :
                            <><Button onClick={() => this.setState({ selectedID: null })}>No</Button><Button >Yes</Button></>}</td>
                    </tr>
                )
            }
        })
    }
}






export default Profile;
