import React from "react";
import { Link, Redirect } from "react-router-dom";
import { connect } from 'react-redux';
import { URL_API } from "../helper";
import Axios from 'axios'
import { NavItem } from "reactstrap";

class Cart extends React.Component {
  

  state = {
    dbcart: [],
  }
  


  getData = () => {
    Axios.get(`${URL_API}/cart/getCart/1`)
    .then(res => {
      this.setState({ dbcart: res.data.results })
    })
    .catch(err => {
      alert("Cannot Get Data")
      console.log(err)
    })
  }

componentDidMount() {
  this.getData()

}

printData = () =>{
  
}


  render() {
    console.log(this.state.dbcart);
    return this.state.dbcart.map((item, index) =>{



    return (
      <div className="p-5 text-center">
        <h1>Cart</h1>
        <div className="row mt-5">
          <div className="col-9 text-center">
            <table className="table">
              <thead className="thead-light">
                <tr>
                  <th>Product</th>
                  <th>Price</th>
                  <th>Image</th>
                  <th>Quantity</th>
                  <th>Total Price</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                {/* {this.renderCart()} */}
                <tr>
                  <td className="align-middle">
                    {item.product_name}
                  </td>
                  <td className="align-middle">
                  {item.product_price}
                  </td>
                  <td className="align-middle">
                  {item.product_image}
                  </td>
                  <td className="align-middle">
                  {item.cart_qty}
                  </td>
                  <td className="align-middle">
                  {item.product_price}
                  </td>
                  <td className="align-middle">
                    <button className="btn btn-danger">
                      Delete
                    </button>
                  </td>
                </tr>
              </tbody>
              <tfoot className="bg-light">
                <tr>
                  <td colSpan="6">
                    <button onClick={this.checkoutModeToggle} className="btn btn-success">
                      Checkout
                    </button>
                  </td>
                </tr>
              </tfoot>
            </table>
          </div>
        </div>
      </div>
    )
  })
  }
}

const mapStateToProps = (state) => {
  return {
    userGlobal: state.user
  }
}

export default connect(mapStateToProps)(Cart);
