import React from 'react';
import Axios from 'axios';
import { Col, Button, Form, FormGroup, Label, Input, FormText, Table } from 'reactstrap';


class Profile extends React.Component{
       

constructor(props) {
    super(props);
    this.state = {
        pharma2: [],
        selectedID: null
    }
}

componentDidMount() {
    this.getData()
}

getData = () => {
    Axios.get(`http://localhost:3300/user/getProfile/1`)
        .then(res => {
            console.log(res.data)
            this.setState({ pharma2 : res.data })
        })
        .catch(err => {
            console.log(err)
        })
}


printData = () => {
    return this.state.pharma2.map((item, index) => {
        if (this.state.selectedID !== index) {
            return (
                <tr>
                    <td>{index + 1}</td>
                    <td>{item.username}</td>
                    <td>{item.email}</td>
                    <td>{item.address}</td>
                    <td>{item.phone_number}</td>
                    <td>{item.full_name}</td>
                    <td>{item.gender}</td>
                    <td>{item.age}</td>
                    <td><Button onClick={() => this.setState({ selectedID: index })}>Edit </Button><Button >Delete</Button></td>
                </tr>
            )
        } else {
            return (
                <div className="row m-auto">
                    <Form width="90vw" className="col-md-2">
                
                    </Form>
                </div>
               
            )
        }
    })
}

render() {
    //    console.log( this.posisi)
    return (
        <div className="row m-auto">
            
            <div className="col-md-10">
                <Table >
                   
                  
                    <tbody>
                        {this.printData()}
                    </tbody>
                </Table>
            </div>
        </div>
    );
}
}



export default Profile;
