import React from "react";
import {
  Navbar,
  Nav,
  NavItem,
  NavLink,
  UncontrolledDropdown,
  DropdownToggle,
  NavbarBrand,
  NavbarText,
  DropdownMenu,
  DropdownItem,
} from "reactstrap";
import { Link } from "react-router-dom";
import { connect } from "react-redux";


class MyNavbar extends React.Component {
  render() {
    return (
      <div>
        <Navbar style={{color:"red"}} color="light" light>
          <NavbarBrand>
            <Link to="/" className="mx-5 nav-" style={{ textDecoration: "none", color: "inherit" }}>
              PHARMA2
            </Link>
          </NavbarBrand>
          <Nav>
            {this.props.userGlobal.username ? (
              <>
                <NavItem>
                  <NavbarText>
                    hello, {this.props.userGlobal.username}
                  </NavbarText>
                </NavItem>
                <UncontrolledDropdown nav inNavbar>
                  <DropdownToggle nav caret>
                    pages
                  </DropdownToggle>
                  <DropdownMenu right>
                    <DropdownItem>
                      <Link to="/cart">
                        {" "}
                        cart({this.props.cartGlobal.cartList.length})
                      </Link>
                    </DropdownItem>
                    <DropdownItem>
                      <Link to="/home"> home</Link>
                    </DropdownItem>
                    <DropdownItem>
                      <Link to="/history"> history</Link>
                    </DropdownItem>
                    {this.props.userGlobal.role === "admin" ? (
                      <DropdownItem>
                        <Link to="/admin"> Admin</Link>
                      </DropdownItem>
                    ) : null}
                  </DropdownMenu>
                  <DropdownItem divider />
                  <DropdownItem onClick={this.props.logoutUser}>
                    log out
                  </DropdownItem>
                </UncontrolledDropdown>
              </>
            ) : (
              <NavItem>
                <NavbarText>
                  <Link to="/login">login</Link> |
                  <Link to="/register">register</Link>
                </NavbarText>
              </NavItem>
            )}
          </Nav>
        </Navbar>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    userGlobal: state.user,
    cartGlobal: state.cart,
  };
};

const mapDispatchToProps = {
  
};

export default connect(mapStateToProps, mapDispatchToProps)(MyNavbar);
