import Axios from 'axios'
import React from 'react'
import {URL_API} from '../../helper'


class Register extends React.Component {
    state = {
       username : "",
       email : "",
       password : ""
    }

    inputHandler = (event) => {
        const value = event.target.value;
        const name = event.target.name;
    
        this.setState({ [name]: value });
      };


   

    onBtnRegister = () => {
        const {username, email, password } = this.state;
    Axios.post(`${URL_API}/user/register`, {
      username,
      email,
      password,
    })
        
         //proses asyncronus
      .then(() => {
        alert("berhasil mendapatkan users");
      })
      .catch(() => {
        alert("gagal mendapatkan users");
      });
    }
        

       

    

    render(){
        return (
            <div className="container" >
                <div className="row">
                    <div className="col-12 text-center my-5" style={{backgroundColor:"grey"}}>
                        <h1>Register page</h1>
                    </div>
                </div>
                <div className="row mt-5">
                    <div className="col-4 offset-4">
                        <div className="card">
                            <div className="card-body">
                                <h5 className="font-weight-bold mb-3">register</h5>
                                
                    
                                        <input
                                        name="username"
                                        onChange={this.inputHandler}
                                        placeholder="username"
                                        type="text"
                                        className="form-control my-2"
                                        />
                                        <input
                                        name="email"
                                        onChange={this.inputHandler}
                                        placeholder="email"
                                        type="text"
                                        className="form-control my-2"
                                        />
                                        <input
                                        name="password"
                                        onChange={this.inputHandler}
                                        placeholder="password"
                                        type="password"
                                        className="form-control my-2"
                                        />
                                <div className="d-flex flex-row justify-content-between align-items-center">
                                    <button onClick={this.onBtnRegister} className="btn btn-primary mt-2">
                                        REGISTER
                                    </button>
                                </div>
                            </div>
                        </div>
                    </div>
                
                </div>
            </div>
            
        )
    }
    
}
export default Register
