import React from "react";
import { Link } from "react-router-dom";
import "bootstrap/dist/css/bootstrap.css";
import { URL_API } from "../../helper";
import Axios from "axios";

class ProductUsage extends React.Component {
    state ={
        dbProductUsage : []
    }

    getData = () => {
        Axios.get(`${URL_API}/admin/get-product-usage`)
          .then((res) => {
            console.log(res.data)
            this.setState({
              dbProductUsage: res.data.results
            });
          })
          .catch((err) => {
            alert("Cannot Get Data");
            console.log(err);
          });
      };

      componentDidMount(){
          this.getData()
      }
    render (){
        console.log(this.state.dbProductUsage);

        return (
            <section class="ftco-section bg-light">
            <div className="container">
                <div className="row justify-content-center pb-5 mb-3">
                    <div className="col-md-7 heading-section text-center ftco-animate fadeInUp ftco-animated">
                        <h2>Affordable Packages</h2>
                    </div>
                </div>
                <div className="row">
            
                    <div className="col-md-4 ftco-animate fadeInUp ftco-animated">
                        <div className="block-7">
                            <div className="img">
                                <img style={{borderTopLeftRadius:"20px", borderTopRightRadius : "20px"}}  src="https://via.placeholder.com/350x280/FFB6C1/000000" alt="" />
                            </div>
                            <div className="text-center p-4" style={{ backgroundColor:"#ADD8E6"}}>
                                <span className="excerpt d-block">Personal</span>
                                <span className="price"><sup>Rp.</sup> <span class="number">5000</span> <sub>/ml</sub></span>
                                <ul className="pricing-text mb-5">
                                    <li><span className="fa fa-check mr-2"></span>QTY Usage = 500ml</li>
                                    <li><span className="fa fa-check mr-2"></span>3 Vet Visit</li>
                                    <li><span className="fa fa-check mr-2"></span>3 Pet Spa</li>
                                    <li><span className="fa fa-check mr-2"></span>Free Supports</li>
                                </ul>
                                <a href="#" className="btn btn-primary d-block px-2 py-3">Get Started</a>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
            </section>
        )
    }

}


export default ProductUsage;
