const init_state = {
    getUs
  };
  
  const reducer = (state = init_state, action) => {
    switch (action.type) {
      case "USER_LOGIN":
        return { ...state, ...action.payload };
      case "USER_ERROR":
          return { ...state, errMsg: action.payload };
      default:

        return state;
    }
  };
  
  export default reducer;
