import React from "react";
import { Link, Redirect } from "react-router-dom";
import { connect } from 'react-redux';
import { URL_API } from "../helper";
import Axios from 'axios'
import { NavItem, Button, Input } from "reactstrap";
const moment = require('moment')


class Transaction extends React.Component{

    state = {
        id_transaction : 0,
        dbTransaction : [],
        isPaidClicked : false,
        status : "all",
        image : "",
        page : 1,
        maxPage : 1,
        limitPage : 0
    }

    inputHandler = (e) =>{
        const value = e.target.value 
        const name = e.target.name 
        this.setState({[name] : value})

    }

    previewFile = (e) =>{
        if(e.target.files[0]){
            this.setState({image : e.target.files[0].name, addFile : e.target.files[0]})
            let preview = document.getElementById("image")
            preview.src = URL.createObjectURL(e.target.files[0])
        }
    }

    getData = () =>{
        if(this.state.status == "all"){

            Axios.get(`${URL_API}/transaction/getTransaction/1/${this.state.limitPage}`)
            .then(res =>{
                this.setState({
                    dbTransaction : res.data,
                    maxPage : Math.ceil(this.state.dbTransaction.length / 5)
                
                })
        
            })
            .catch(err =>{
                alert("cannot get data")
                console.log(err);
            })
        }else if(this.state.status == "unpaid" || "process" || "shipping" || "done"){
            Axios.get(`${URL_API}/transaction/getTransactionFilter/1/${this.state.status}`)
            
            .then(res =>{
            
                this.setState({
                    dbTransaction : res.data,
                
                })
        
            })
            .catch(err =>{
                alert("cannot get data")
                console.log(err);
            })
        }
            
       
    }
    nextPageHandler = () =>{
        this.setState({page : this.state.page + 1 , limitPage : this.state.limitPage + 5})
    }
    
    prevPageHandler = () =>{
        this.setState({page : this.state.page - 1 , limitPage : this.state.limitPage - 5})

    }

    onBtnUploadPayment = () =>{
        if(this.state.addFile){
            let formData = new FormData()

            formData.append('file', this.state.addFile)
            Axios.patch(`${URL_API}/upload/uploadPayment/${this.state.id_transaction}`, formData)
            .then((res)=>{
                alert(res.data.message)
                this.setState({isPaidClicked : false})
                this.getData()
            })
            .catch((err)=>{
                console.log(err);
            })
        }

    }

    renderTransactionDetailItems = () => {
        // return this.state.transactionDetails.map((val) => {
          return (
            <div className="card-body">
              <div className="d-flex my-2 flex-row justify-content-between align-items-center">
                <span className="font-weight-bold">
                  jhgfeabhdfsbkj
                </span>
                <span>Rp 30000</span>
              </div>
            </div>
          );
        // });
      };

      paymentCard = () =>{
        <div className="col-md-3 p-4 text-white text-left" style={{backgroundColor:"#00008B", borderRadius: "30px"}}>
        <form>
            
            <div className="form-group">
                
                <div>
                    <img 
                    src = "https://lh3.googleusercontent.com/proxy/mppUAaY6fJPg5XkNbL9wsLSN9b5ADfsMG3leMdIXJQwQZCCkf987pkaXGAU2f7HXprlazKOqgCZwwmg_r3JxxGovzHuzNPG_BO-tuqsHZFs"
                    id = "image"
                    alt=""
                    className= "img-thumbnail d-grid gap-2 col-9 mx-auto" 
                    style={{width:"700px", height:"500px", backgroundColor:"white"}}/>
                </div>
                
            </div>
            <div className="form-group">
                <label htmlFor="img">Image</label>
                <input type="file" className="form-control" id="img" aria-describedby="emailHelp" onChange= {this.previewFile} />
            </div>
        </form>
        <button type="button" onClick={this.onBtnUploadPayment}  className="btn btn-primary float-right" >Add Data</button>
        <button type="button"  onClick={this.onBtnCancel} className="btn btn-warning float-right mx-3" >Cancel</button>
    </div>

      }

    componentDidUpdate(){
        this.getData()
    }

    componentDidMount() {
        this.getData()
    }

    onBtnPay = (idTr)=>{
        this.setState({id_transaction : idTr,
                    isPaidClicked : true})
        this.getData()
    }
    onBtnCancel = () =>{
        this.setState({
            isPaidClicked : false
        })
    }






    printData = () =>{
        return this.state.dbTransaction.map((item, index) =>{
            
          
            console.log(this.state.status)
          return(
            <tr>
              <td className="align-middle">
                  {index+1}
                </td>
                <td className="align-middle">
                    {moment(item.date).format("MMM / D / YYYY")}
                </td>
                <td className="align-middle">
                {item.total_qty}
                </td>
                <td className="align-middle">
                Rp.{item.tax}
                </td>
                <td className="align-middle">
                Rp.{item.total_tp}
                </td>
                <td className="align-middle">
                {item.expedition_name}
                </td>
                <td className="align-middle">
                Rp.{item.shipping_cost}
                </td>
                <td className="align-middle">
                Rp.{item.final_price}
                </td>
                <td className="align-middle">
                {item.status}
                </td>
                <td className="align-middle">
                    {
                    item.status == "unpaid" ? 
                    <button className="btn btn-primary" onClick={() =>this.onBtnPay(item.id_transaction)} >pay</button> :
                    <button className="btn btn-success"  >Detail</button>
                    }
                </td>
              </tr>
          )
        })
      
      }
    render(){
        
        return(
            <div className="p-5 text-center">
        <h1>Transaction</h1>
        <div className="row mt-5">
          <div className="col-9 text-center">
              Status : 
          <Input
                className={" btn btn-primary my-3"}
                name = "status"
                onChange ={ this.inputHandler}
                style={{ width: "100px" }}
                type="select"
                id="exampleSelect"
                >
                <option value={"all"}>All</option>
                <option value={"unpaid"}>Unpaid</option>
                <option value={"process"}>Process</option>
                <option value={"shipping"}>Shipping</option>
                <option value={"done"}>Done</option>
                </Input>
            <table className="table">
              <thead className="thead-dark">
                <tr>
                  <th>no</th>
                  <th>Date</th>
                  <th>Quantity</th>
                  <th>Tax</th>
                  <th>Price</th>
                  <th>Expedition</th>
                  <th>Shipping Cost</th>
                  <th>Total Price</th>
                  <th>Status</th>
                  <th>Action</th>
                </tr>
              </thead>
              <tbody>
                {/* {this.renderCart()} */}
                {this.printData()}
              </tbody>
              <tfoot className="bg-light">
                <tr>
                  <td colSpan="10">
                    
              <div className="d-flex flex-row justify-content-center align-items-center">
                <button
                  disabled={this.state.page === 1}
                  className="btn btn-dark"
                  onClick={this.prevPageHandler}
                >
                  {"<"}
                </button>
                <div className="text-center">
                  page {this.state.page}
                </div>
                <button
                  disabled={this.state.dbTransaction.length < 5}
                  className="btn btn-dark"
                  onClick={this.nextPageHandler}
                >
                  {">"}
                </button>
              </div>
                  </td>
                </tr>
              </tfoot>
            </table>
          </div>
          <div className="card-body">
                  {this.renderTransactionDetailItems()}
         </div>
          {
              this.state.isPaidClicked ? (
                  this.paymentCard
                
              ) : null
          }
         
          
        </div>
      </div>
        )
    }
    
}
export default Transaction
