import React from "react";
import { Link, Redirect } from "react-router-dom";
import { connect } from 'react-redux';
import { URL_API } from "../../helper";
import Axios from 'axios'
import { NavItem, Button, Input } from "reactstrap";
i


class CustomTransaction extends React.Component{

    state = {
        dbCustomTransaction : [],
        
    }

    inputHandler = (e) =>{
        const value = e.target.value 
        const name = e.target.name 
        this.setState({[name] : value})

    }

    getData = () => {
        Axios.get(`${URL_API}/admin/custom-order`)
          .then((res) => {
            this.setState({
              dbCustomTransaction: res.data.results,
            });
          })
          .catch((err) => {
            alert("Cannot Get Data");
            console.log(err);
          });
      };

      componentDidMount (){
          this.getData()
      }

  



    printData = () =>{
        return this.state.dbCustomTransaction.map((item, index) =>{
          return(
            <tr>
              <td className="align-middle">
                  {index+1}
                </td>
                <td className="align-middle">
                    {item.username}
                </td>
                <td className="align-middle">
                {item.commentar}
                </td>
              </tr>
          )
        })
      
      }
    render(){
        
        return(
            <div className="p-5 text-center">
        <h1> custom Transaction</h1>
        <div className="row mt-5">
          <div className="col-9 text-center">
         
            <table className="table">
              <thead className="thead-dark">
                <tr>
                  <th>username</th>
                  <th>Commentar</th>
                </tr>
              </thead>
              <tbody>
                {this.printData()}
              </tbody>
              <tfoot className="bg-light">
                <tr>
                  <td colSpan="10">
                    
              <div className="d-flex flex-row justify-content-center align-items-center">
                <button
                  disabled={this.state.page === 1}
                  className="btn btn-dark"
                  onClick={this.prevPageHandler}
                >
                  {"<"}
                </button>
                <div className="text-center">
                  page {this.state.page}
                </div>
                <button
                  disabled={this.state.dbTransaction.length < 5}
                  className="btn btn-dark"
                  onClick={this.nextPageHandler}
                >
                  {">"}
                </button>
              </div>
                  </td>
                </tr>
              </tfoot>
            </table>
          </div>
          {this.state.isDetailClicked ?
          <div className="card" style={{width : "300px"}}>
                <div className="card-header">
                  <strong>transaction detail</strong>
                </div>
                <div className="card-body">
                  {this.cardDetail()}
                </div>
                <button onClick={this.onBtnBackDetail} className="btn btn-primary mx-5 my-3">back</button>
              </div>
              :
              null
          }
        
        </div>
      </div>
        )
    }
    
}
export default CustomTransaction
